<script>
  import { onMount } from "svelte";
  import { gsap } from "gsap";
  import { ScrollTrigger } from "gsap/ScrollTrigger";
  import { select } from "d3";

  let width = 1000;
  let height = 1000;
  let gWidth;
  let gHeight;
  let translateX;
  let translateY;
  const ids = [
    "#blue",
    "#purple",
    "#yellow",
    "#pink",
    "#darkorange",
    "#orange",
    "#black1",
    "#black2",
    "#brown",
    "#green",
  ];
  // Define the number of objects
  const numberOfObjects = ids.length;

  // Define the radius of the circle
  const radius = 150;

  // Calculate the angle between each object
  const angleIncrement = (2 * Math.PI) / numberOfObjects;

  // Initialize an array to store the positions of the objects
  const positions = [];

  // Calculate the positions using sine and cosine
  for (let i = 0; i < numberOfObjects; i++) {
    const angle = i * angleIncrement;
    const x = radius * Math.cos(angle);
    const y = radius * Math.sin(angle);
    positions.push({ x, y });
  }

  onMount(() => {
    gWidth = select("#wrapper").node().getBBox().width;
    gHeight = select("#wrapper").node().getBBox().height;

    // const dims = ids.map((d) => {
    //   let elWidth = d3.select(d).node().getBBox().width;
    //   let elHeight = d3.select(d).node().getBBox().height;
    //   console.log(elWidth);
    //   return { elWidth, elHeight };
    // });

    // Calculate translation values to center the <g> element
    translateX = (width - gWidth) / 2 - select("#wrapper").node().getBBox().x;
    translateY = (height - gHeight) / 2 - select("#wrapper").node().getBBox().y;

    gsap.registerPlugin(ScrollTrigger);

    ScrollTrigger.create({
      trigger: "#headerSection",
      start: "top top",
      end: "bottom 150px",
      pin: "#svg",
      // markers: true,
    });

    gsap.to("#title", {
      opacity: 0,
      scrollTrigger: {
        trigger: "#headerSection",
        start: "top top",
        end: "bottom bottom",
        scrub: true,
      },
    });

    ids.forEach((d, i) => {
      // d3.select(d).attr(
      //   "transform",
      //   `translate(${positions[i].x},${positions[i].y})`
      // );
      gsap.to(d, {
        x: "+=" + positions[i].x,
        y: "+=" + positions[i].y,
        scale: 0.5,
        ease: "none",
        transformOrigin: "center center",
        scrollTrigger: {
          trigger: "#svg",
          start: "top top",
          end: "bottom center",
          scrub: true,
          // markers: true,
        },
      });
    });
  });
</script>

